// Fixed fragments - same for every user, same positions every time
function getRandomFragments(count, includePunctuation = true) {
    let pool = essayData.filter(item => !item.isPunctuation);
    let punctuation = essayData.filter(item => item.isPunctuation);

    // NO SHUFFLING - use fixed order so every user sees the same bubbles in same positions
    let selected = pool.slice(0, count);

    // Add punctuation at fixed positions
    if (includePunctuation) {
        let numPunctuation = Math.floor(count * 0.15);
        // Insert punctuation at predetermined positions (always the same)
        let punctPositions = [5, 12, 18, 23]; // Fixed positions

        for (let i = 0; i < numPunctuation && i < punctPositions.length; i++) {
            let punctIndex = i % punctuation.length;
            let insertPos = punctPositions[i] % selected.length;
            selected.splice(insertPos, 0, punctuation[punctIndex]);
        }
    }

    return selected;
}
